import { computed, onMounted } from 'vue';
import { useStore } from 'vuex';
import NewsCard from '../components/NewsCard.vue';

// Vuexストアのインスタンスを取得

export default {
  __name: 'News',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const store = useStore();

    // Vuexストアのstateを取得してリアクティブに扱う
    const news = computed(() => store.state.news);
    const error = computed(() => store.state.error);

    // ニュースを取得するメソッド
    const getNews = () => {
      store.dispatch('fetchNews');
    };

    // コンポーネントがマウントされたときにニュースを取得
    onMounted(() => {
      getNews();
    });
    const __returned__ = {
      store,
      news,
      error,
      getNews,
      computed,
      onMounted,
      get useStore() {
        return useStore;
      },
      NewsCard
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
