import { createStore } from 'vuex';
import axios from 'axios';

const store = createStore({
  state: {
    news: [],
    error: null
  },
  mutations: {
    setNews(state, news) {
      state.news = news;
    },
    setError(state, error) {
      state.error = error;
    }
  },
  actions: {
    async fetchNews({ commit }) {
      try {
        const response = await axios.get('https://content.guardianapis.com/search?api-key=test');
        commit('setNews', response.data.response.results);
        commit('setError', null);
      } catch (error) {
        commit('setError', 'Error fetching news');
        commit('setNews', []);
      }
    }
  }
});

export default store;
